import React, { useRef, useState, useEffect } from 'react';

const TodoItem = ({ todo, onDelete, onToggle }) => {
  const [isOverflowing, setIsOverflowing] = useState(false);
  const todoRef = useRef(null);

  useEffect(() => {
    const element = todoRef.current;
    if (element) {
      setIsOverflowing(element.scrollWidth > element.clientWidth);
    }
  }, [todo.text]);

  const handleDelete = () => {
    const confirmDelete = window.confirm(`Are you sure you want to delete "${todo.text}"?`);
    if (confirmDelete) {
      onDelete(todo.id);
    }
  };

  return (
    <div className="todo-item">
      <span ref={todoRef}>{todo.text}</span>
      <div className="button-container">
        <button 
          onClick={() => onToggle(todo.id)}
          style={{
            backgroundColor: todo.completed ? '#2ecc71' : '#3498db',
            color: '#fff',
            border: 'none',
            padding: '8px 12px',
            borderRadius: '4px',
            cursor: 'pointer',
            transition: 'background-color 0.3s'
          }}
        >
          {todo.completed ? 'Completed' : 'Mark Complete'}
        </button>
        <button onClick={handleDelete} style={{ backgroundColor: '#ff6b6b', color: '#fff', border: 'none', padding: '8px 12px', borderRadius: '4px', cursor: 'pointer', transition: 'background-color 0.3s' }}>
          Delete
        </button>
      </div>
      {isOverflowing && <div className="popup">{todo.text}</div>}
    </div>
  );
};

export default TodoItem;
