import React from 'react';

const TodoItem = ({ todo, onDelete, onToggle }) => {
  return (
    <div className="todo-item">
      <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
        {todo.text}
      </span>
      <button 
        onClick={() => onToggle(todo.id)}
        style={{
          backgroundColor: todo.completed ? '#2ecc71' : '#3498db',
          color: '#fff',
          border: 'none',
          padding: '8px 12px',
          borderRadius: '4px',
          cursor: 'pointer',
          transition: 'background-color 0.3s'
        }}
      >
        {todo.completed ? 'Completed' : 'Mark Complete'}
      </button>
      <button onClick={() => onDelete(todo.id)} style={{ backgroundColor: '#ff6b6b', color: '#fff', border: 'none', padding: '8px 12px', borderRadius: '4px', cursor: 'pointer', transition: 'background-color 0.3s' }}>
        Delete
      </button>
    </div>
  );
};

export default TodoItem;
