import React, { useState, useEffect } from "react";
import TodoList from "./TodoList";
import TodoForm from "./TodoForm";
import "./Todo.css";
const Todo = () => {
  const [todos, setTodos] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    const storedTodos = localStorage.getItem("todos");
    if (storedTodos) {
      setTodos(JSON.parse(storedTodos));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const addTodo = (text) => {
    const newTodo = {
      id: Date.now(),
      text,
      completed: false,
    };
    setTodos([...todos, newTodo]);
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id)); // try delete using pop
  };

  const toggleTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const filteredTodos = todos.filter((todo) => {
    // Trim spaces from the todo text
    const trimmedTodoText = todo.text.trim().toLowerCase();
    // Split the search query into words
    const searchWords = searchQuery.toLowerCase().split(/\s+/);

    // Check if all words in the search query are present in the todo text
    return (
      searchWords.every((word) => trimmedTodoText.includes(word)) &&
      (filter == "all" ||
        (filter == "completed" && todo.completed) ||
        (filter == "incomplete" && !todo.completed))
    );
  });

  // const filteredTodos = todos.filter(
  //   (todo) =>
  //     todo.text.toLowerCase().includes(searchQuery.toLowerCase()) &&
  //     (filter == "all" ||
  //       (filter == "completed" && todo.completed) ||
  //       (filter == "incomplete" && !todo.completed))
  // );

  return (
    <div className="container">
      <h1>Todo App</h1>
      <TodoForm onAdd={addTodo} />
      <div className="filters">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value.trim())}
          placeholder="Search Todos..."
          className="search-input"
        />
        <div className="filter-buttons">
          <button
            className={filter === "all" ? "active" : ""}
            onClick={() => setFilter("all")}
          >
            All
          </button>
          <button
            className={filter === "completed" ? "active" : ""}
            onClick={() => setFilter("completed")}
          >
            Completed
          </button>
          <button
            className={filter === "incomplete" ? "active" : ""}
            onClick={() => setFilter("incomplete")}
          >
            Incomplete
          </button>
        </div>
      </div>
      <TodoList
        todos={filteredTodos}
        onDelete={deleteTodo}
        onToggle={toggleTodo}
      />
    </div>
  );
};

export default Todo;

